mapper.select:
SELECT *
FROM MENU
WHERE RST_NO=1 AND MENU_NO=#{menuNo}

mapper.update:
UPDATE MENU
SET MENU_NAME=#{menuName}, MENU_PRICE=#{menuPrice}, MENU_CNT=#{menuCnt}, MENU_IMG=#{menuImg}
WHERE RST_NO=1 AND MENU_NO=#{menuNo}

mapper.delete:
DELETE
FROM SEATPLACE
WHERE RST_NO=1 AND RST_TBL=#{rstTbl}
